import React, { useState, useEffect } from 'react';
// import FrontEnd from '../images/Saleh, Waleed.png';
// import FullStack from '../images/Waleed_Saleh_FULL STACK WEB DEVELOPMENT1024_1.jpg';
import './certify.css';

export const Slideshow = ({ images = [], interval = 3000 }) => {
	const [thumbnail, setThumbnail] = useState([]);
	const [current, setCurrent] = useState(0);
	const [slideStyle, setSlideStyle] = useState({});

	useEffect(() => {
		setThumbnail(images);
		setSlideStyle({
			backgroundImage: "url('" + images[current] + "')",
		});
        const loop = setInterval(() => {
            if(current === images.length-1){
                setCurrent(0);
            }
        })
	}, [images, current]);

	function previous() {
		if (current) {
			setCurrent(current - 1);
		} else {
			setCurrent(thumbnail.length - 1);
		}
	}

	function next() {
		if (current === thumbnail.length - 1) {
			setCurrent(0);
		} else {
			setCurrent(current + 1);
		}
	}

	return (
		<section className='slideshow'>
			<div className='slide-holder'>
				<section className='slide previous'>
					<div className='thumbnail'></div>
				</section>
				<section className='slide current'>
					<div style={slideStyle} className='thumbnail'></div>
				</section>
				<section className='slide next'>
					<div className='thumbnail'></div>
				</section>
			</div>
			<div className='controller'>
				<span onClick={previous}>Previous</span>
				<span onClick={next}>Next</span>
			</div>
		</section>
	);
};
