import './certify.css';

export const Certify = (images = [], interval = 3000) => {
	const [thumbnail, setThumbnail] = useState([]);
	const [current, setCurrent] = useState(0);
	const [slideStyle, setSlideStyle] = useState({});

	useEffect(() => {
		setThumbnail(images);
		setSlideStyle({
			backgroundImage: "url('" + images[current] + "')",
		});
	}, [images, current]);

	return <Slideshow />;
};

